$('.photomag-slider').map(function(){

		var $th = $(this);
		var l = $(this).attr('data-tag');
		var n = $(this).attr('data-no');
        var c = '';
        $.ajax({
           url : "/feeds/posts/default/-/" + l + "?alt=json-in-script&max-results=" + n,
           type: 'get',
           dataType: "jsonp",
           success: function(e) {
             if(e.feed.entry){
               for(var i=0;i < e.feed.entry.length;i++){
                 var k = e.feed.entry[i];

                 //get the image
                 if(k.media$thumbnail){
                   var pi = k.media$thumbnail.url;

                   //check for the location for thumbnail
                   if(pi.indexOf('/s72-c') !== -1){
                     pi = pi.replace('/s72-c','/s1600');
                   }
                   if(pi.indexOf('img.youtube.com') !== -1){
                     pi = pi.replace('/default.jpg','/maxresdefault.jpg');
                   }

                 }else{
                   var pi = 'https://4.bp.blogspot.com/-wPwjv7-YYGY/Wc98wlDT8qI/AAAAAAAAAEE/mH8YkPl8qJAH9FMuFKcShQvXXYMmVyrIgCLcBGAs/s1600/notfound.png';
                 }



                 //get the category

                 var cat = k.category[0].term;
                 var catu = '<a href="/search/label/'+ cat +'?&max-results=' + perpage + '" title="Show all posts in '+ cat +'">'+ cat +'</a>';


                 //description
                 function snip(cont, size_desc) {
                   return cont.replace(/<.*?>/gi, "").split(/\s+/).slice(0, size_desc - 1).join(" ");
                 }


                 var snipp = snip(k.content.$t,25);
                 //get the title
                 var pt = k.title.$t;



                 //get the url
                 for(var xc=0;xc<k.link.length;xc++){
                   if(k.link[xc].rel == 'alternate'){
                     var pu = k.link[xc].href;
                   }
                 }

                 //create slide markup
                 c = c + '<div class="slide item">\
<div class="slide-wrapper" style="background:url(\''+ pi +'\')">\
<div class="slide-inner">\
<div class="slide-box">\
<div class="slidebox-inner">\
<div class="slide-category">'+ catu +'</div>\
<div class="slide-title"><h3><a href="'+ pu +'">'+ pt +'</a></h3></div>\
    </div>\
    </div>\
    </div>\
    </div>\
    </div>';
               }//for

				$('#slider_main').html(c);
               $th.closest('.hidden').removeClass('hidden');

               }//if
			},
          complete: function(){

          $('#slider_main').owlCarousel({
          loop:true,
          margin:10,
            autoplay : true,
          nav:false,
			animateOut: 'fadeOut',
            dots : true,
          responsive:{
            0:{
               items:1
            },
           600:{
               items:1
               },
               1000:{
               items:1
               }
               }
               });
        }
        });//ajax

